content:
h1 {
    /* TODO: Add some cool styles here */
}
.grid {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 10px;
    max-width: 400px;
    margin: 0;
}
.tile img {
    width: 100%;
    max-width: 100px;
    height: auto;
    border: 2px solid #ccc;
    border-radius: 8px;
}

h1 {
    color: teal;
    font-family: "Comic Sans MS", cursive, sans-serif;
    text-shadow: 2px 2px #ffcc00;
    text-align: center;
}
.grid {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 2px;
    max-width: 400px;
    margin: auto;
}
.tile img {
    width: 100%;
    max-width: 120px;
    height: auto;
    border: 2px solid #ccc;
    border-radius: 8px;
}
